import type { RuleGroupType, RuleGroupTypeAny, RuleGroupTypeIC, RuleType } from '../ts';
/**
 * Determines if this is either a RuleGroupType or RuleGroupTypeIC.
 * `'rules' in query` can be used as an alternative.
 */
export declare const isRuleGroup: (rg: RuleType | RuleGroupTypeAny) => rg is RuleGroupTypeAny;
export declare const isRuleGroupType: (rg: RuleType | RuleGroupTypeAny) => rg is RuleGroupType;
export declare const isRuleGroupTypeIC: (rg: RuleType | RuleGroupTypeAny) => rg is RuleGroupTypeIC;
